import { useState, useRef } from 'react';

import classes from './AuthForm.module.css';

const AuthForm = () => {
  const emailInputRef = useRef();
  const passwordInputRef = useRef();
  const [isLogin, setIsLogin] = useState(true);
  const [isLoading, setIsLoading] = useState(false)

  const switchAuthModeHandler = () => {
    setIsLogin((prevState) => !prevState);
  };

  const submitHandler = e => {
    e.preventDefault();

    const enteredEmail = emailInputRef.current.value;
    const enteredPasword = passwordInputRef.current.value;

    //optional: Add Validations (REGEX) [password > 8 characters, contains @#$!%^, at least one number]


    setIsLoading(true)
    if (isLogin) {
      //login logic
    } else {
      //sign up
      setIsLoading(false)
      fetch("https://identitytoolkit.googleapis.com/v1/accounts:signUp?key=AIzaSyAXnD_ob8wbuFKBWW1PDHwceGSljY5n-Uw", 
      {
          method: 'POST',
          body: JSON.stringify({
              email: enteredEmail,
              password: enteredPasword,
              returnSecureToken: true
          }),
          headers: {
            'Content-Type': 'application/json'
          }
      }).then(res => {
        if (res.ok) {
          //do something
          console.log(res.ok)
        } else {
          return res.json().then(data => {
            let errorMessage = 'Authentication Failed';
            if (data && data.error && data.error.message) {
              errorMessage = data.error.message;
            }
            alert(errorMessage)
          })
        }
      })
    }

  }

  return (
    <section className={classes.auth}>
      <h1>{isLogin ? 'Login' : 'Sign Up'}</h1>
      <form onSubmit={submitHandler}>
        <div className={classes.control}>
          <label htmlFor='email'>Your Email</label>
          <input type='email' id='email' required ref={emailInputRef}/>
        </div>
        <div className={classes.control}>
          <label htmlFor='password'>Your Password</label>
          <input type='password' id='password' required ref={passwordInputRef} />
        </div>
        <div className={classes.actions}>
          {!isLoading && <button>{isLogin ? 'Login' : 'Create Account'}</button>}
          {isLoading && <p>Sending Request...</p>}
          <button
            type='button'
            className={classes.toggle}
            onClick={switchAuthModeHandler}
          >
            {isLogin ? 'Create new account' : 'Login with existing account'}
          </button>
        </div>
      </form>
    </section>
  );
};

export default AuthForm;
